import React, { useContext } from "react";
import { useState } from "react";
import context from "react-bootstrap/esm/AccordionContext";
import { Button, Col, Dropdown, Form, Modal, Row } from "react-bootstrap";
import { Context } from "../../index";

const CreateHouse = ({ show, onHide }) => {
  const { device } = useContext(Context);
  const [name, setName] = useState('')
  const [price, setPrice] = useState(0)
  const [file, setFile] = useState(null)
  const [type, setType] = useState(null)
  const [brand, setBrand] = useState(null)

  const [info, setInfo] = useState([]);

  const addInfo = () => {
    setInfo([...info, {title:'', description:'', number: Date.now()}])
  }
  const removeInfo = (number) => {
    setInfo(info.filter(i => i.number !== number))
  }

  const selectFile = e => {
    console.log(e.target.files)

  }


  return (
    <Modal show={show} onHide={onHide}>
      <Modal.Header closeButton>
        <Modal.Title>Добавить новый дом</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          <Dropdown>
            <Dropdown.Toggle className="mt-2">
              Выберите тип дома
            </Dropdown.Toggle>
            <Dropdown.Menu>
              {device.types.map((type) => (
                <Dropdown.Item key={type.id}>{type.name}</Dropdown.Item>
              ))}
            </Dropdown.Menu>
          </Dropdown>
          <Dropdown>
            <Dropdown.Toggle className="mt-2">
              Выберите компанию
            </Dropdown.Toggle>
            <Dropdown.Menu>
              {device.brands.map((brand) => (
                <Dropdown.Item key={brand.id}>{brand.name}</Dropdown.Item>
              ))}
            </Dropdown.Menu>
          </Dropdown>
          <Form.Control
            className="mt-2"
            placeholder="Введите название нового дома"
          />
          <Form.Control
            className="mt-2"
            placeholder="Введите стоимость нового дома"
            type="number"
          />
          <Form.Control
            className="mt-2"
            placeholder="Введите фото нового дома"
            type="file"
            onC
          />
          <hr />
          <Button variant="outline-dark" onClick={addInfo}>Добавить новое описание</Button>
          {info.map(i => 
              <Row key={i.number} className="d-flex mt-2">
                <Col md={4}>
                  <Form.Control placeholder="Введите название"/>
                </Col>
                <Col md={4}>
                <Form.Control placeholder="Введите описание"/>
                </Col>
                <Col md={4}>
                <Button
                onClick={() => removeInfo(i.number)} 
                variant="outline-danger">
                  Удалить
                </Button>
                </Col>
              </Row>
            )}
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="outline-dark" onClick={onHide}>
          Закрыть
        </Button>
        <Button variant="outline-success" onClick={onHide}>
          Сохранить
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default CreateHouse;
