import React from "react";
import { Button, Form, Row, Col, Card, Image } from "react-bootstrap";
import Container from "react-bootstrap/Container";
import { CiStar } from "react-icons/ci";

const DevicePage = () => {
  const device = {
    id: 1,
    name: "A-Frame в глухом лесу",
    price: 14000,
    rating: 4,
    img: "0731a54a-3640-402c-a35d-9bb26b0d434d.jpg",
  };

  return (
    <Container>
      <Row>
        
      </Row>
      <Col md={4}>
        <Image width={300} src={device.img} />
      </Col>

      <Col md={4}>
        <Row className="d-flex flex-column align-items-center">
          <h2>{device.name}</h2>
          <div className="d-flex align-items-center justify-content-center">
            <CiStar />
            {device.rating}
          </div>
        </Row>
      </Col>

      <Col md={4}>
        <Card 
          className="d-flex align-items-center justify-content-around"
          style={{}}
        >
          <h3>{device.price}руб\сут</h3>
          <Button variant="outline-black">Забронировать</Button>
        </Card>
      </Col>
    </Container>
  );
};

export default DevicePage;
